-- ----------------------------
-- Table structure for course
-- ----------------------------
DROP TABLE IF EXISTS `course`;
CREATE TABLE `course`  (
  `uid` bigint NOT NULL AUTO_INCREMENT COMMENT 'id，唯一标识',
  `course_no` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '课程编号',
  `course_name` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '课程名称',
  `course_brief` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '课程简介',
  `curriculum_time` date NULL DEFAULT NULL COMMENT '开课时间',
  `session_time` date NULL DEFAULT NULL COMMENT '结课时间',
  `course_classify` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '课程分类',
  `teacher_id` bigint NOT NULL COMMENT '授课老师',
  `textbook` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '教材',
  PRIMARY KEY (`uid`, `course_no`, `teacher_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 17 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of course
-- ----------------------------
INSERT INTO `course` VALUES (1, '100001', '数据结构', '《数据结构》课程是计算机科学与技术的学科基础课程，是许多计算机课程的先导课程。', '2020-03-01', '2020-07-01', '计算机', 1, '《数据结构》');
INSERT INTO `course` VALUES (1, '100002', '犯罪心理学', '犯罪心理学（Criminal Psychology）研究作为反映社会的犯罪心理和行为规律的学科。介于自然科学与社会科学之间的跨界学科。既是心理学的一个分支又是犯罪学的一个分支。', '2021-05-02', '2021-05-07', '心理学', 1, '《犯罪心理学》');
INSERT INTO `course` VALUES (1, '100003', '微观经济学', '微观经济学（Microeconomics）(“微观”是希腊文“ μικρο ”的意译，原意是“小\")又称个体经济学，小经济学，是现代经济学的一个分支，主要以单个经济单位（单个生产者、单个消费者、单个市场经济活动）作为研究对象分析的一门学科。', '2018-05-14', '2019-05-17', '经济管理', 3, '《微观经济学》');
INSERT INTO `course` VALUES (1, '100004', '大学英语1', '大学英语基础课程', '2020-05-05', '2021-09-25', '英语', 4, '《大学英语1》');
INSERT INTO `course` VALUES (1, '100005', '大学英语2', '大学英语基础课程', '2020-05-11', '2021-08-20', '英语', 7, '《大学英语2》');
INSERT INTO `course` VALUES (1, '100006', '园林设计', '园林设计就是在一定的地域范围内，运用园林艺术和工程技术手段，通过改造地形（或进一步筑山、叠石、理水），种植树木、花草，营造建筑和布置园路等途径创作而建成美的自然环境和生活、游憩境域的过程。', '2018-05-07', '2019-05-16', '农林园艺', 7, '《园林设计》');
INSERT INTO `course` VALUES (2, '100007', 'C语言程序设计', 'C语言程序设计是计算机基础课程', '2018-05-13', '2021-05-13', '计算机', 2, '《C语言程序设计》');
INSERT INTO `course` VALUES (3, '100008', '操作系统', '操作系统课讲解操作系统中如何管理和协调应用程序对计算机系统中软硬件资源的使用。', '2020-05-18', '2021-01-22', '计算机', 1, '《操作系统课》');
INSERT INTO `course` VALUES (4, '100009', '农药学', '农药是人类与有害生物斗争的法宝，更是世界粮食安全生产和人类健康生活的保障，在病、虫、草、鼠等有害生物的综合治理中具有不可替代的作用。《农药学》是植保专业本科生的专业核心课，同时也是农学、园艺和林学等专业的必修或选修课，具有农业化学与生物学、环境科学、农学等学科交叉特点，兼备理论性和实践性，对从事农药研发、推广应用、流通管理等领域的专业人员和科技工作者也具有较强的学习和参考价值。开启农药学课程学习，中国农业大学植物保护学院二级教授领衔教学团队，将带你一起领略农药学这一交叉学科的风采！', '2020-05-18', '2021-05-15', '农林园艺', 1, '《农药学》');
INSERT INTO `course` VALUES (5, '100010', '大数据基础与应用', '本课程介绍大数据的基础知识、数据分析的基本流程和大数据在各个行业的应用情况；涵盖了编程语言、分析算法、计算框架、数据可视化等众多内容。此外，本课程基于华为公有云提供在线实践内容，使用浏览器即可动手体验大数据分析的各个环节。不会编程？没有计算资源？都不是问题，只要你有时间！', '2019-05-12', '2020-05-21', '计算机', 1, '《大数据基础与应用》');
INSERT INTO `course` VALUES (6, '100011', 'Python语言程序设计', '计算机是运算工具，更是创新平台，高效有趣地利用计算机需要更简洁实用的编程语言。Python简洁却强大、简单却专业，它是当今世界最受欢迎的编程语言，学好它终身受用。请跟随我们，学习并掌握Python语言，一起动起来，站在风口、享受创新！', '2020-05-04', '2021-05-04', '计算机', 1, '《Python语言程序设计》');
INSERT INTO `course` VALUES (7, '100012', '心理学基础', '本课程是首批国家级一流课程（线上线下混合式课程）的在线教学部分。这是一门能听懂、够专业、有兴趣、受启发的心理学课。以事说理——从46个发生在身边的故事说起，让心理学触手可及；以理解惑——基础理论、前沿成果、科学方法融会贯通，从“门外汉”变身“路上人”；深入浅出——用最平实的语言讲授最专业的知识；举一反三——让心理学服务生活，做纷繁世界的明白人。', '2021-03-17', '2021-07-10', '心理学', 1, '《心理学基础》');
INSERT INTO `course` VALUES (8, '100013', '\r\n医学心理学', '医学心理学是医学和心理学的交叉学科，是医学院校各专业本科生的必修课程，也是执业医师考试的重要组成部分。医学心理学不仅有助于临床医务工作者更好地理解患者、治疗患者，也有助于维护良好的医患关系、维护医务人员的心理健康。此外，随着近年来社会大众对心理健康、心身疾病、心理咨询与治疗等领域的关注日益增高，该课程也可以对有兴趣的社会大众开放，提高大众对心理健康的了解、对心理咨询与治疗的接纳。', '2020-06-24', '2021-06-23', '心理学', 1, '《\r\n医学心理学》');
INSERT INTO `course` VALUES (9, '100014', '社会心理学', '“社会心理学”是面向心理学专业本科生和研究生开设的一门专业必修课，历时一学期。课程主要包括经典理论的学习、前沿研究的分享和社会心理学实验设计三个部分。学生通过课程学习，掌握社会心理学领域的重要概念、经典理论与最新研究进展，形成系统、完善的社会心理学知识体系；养成真实问题导向的内在学习动机，结合社会现实运用社会心理学实验方法服务社会、造福大众的实践能力。', '2020-12-22', '2021-06-10', '心理学', 1, '《社会心理学》');
INSERT INTO `course` VALUES (10, '100015', '抑郁症的心理辅导', '抑郁是如何产生的？对人有什么影响？ 为什么青少年会抑郁、厌学、辍学甚至自残、厌世？如何帮助他们？ 老年抑郁、产后抑郁、高压力职业导致的抑郁如何应对？ 工作倦怠，不堪忍受怎么办？ 莫名其妙闷闷不乐，心境低落怎么办？ 觉得活得没意思怎么办？ 如何不让自己想太多？ 失眠很痛苦，如何应对？ 抑郁患者身边的人可以做些什么？ 本课程“抑郁症的心理辅导”将系统解答以上问题，讲授预防与治疗抑郁症的心理学知识与技巧。', '2020-10-13', '2021-05-12', '心理学', 1, '《抑郁症的心理辅导》');
INSERT INTO `course` VALUES (11, '100016', '金融学基础', '你想了解金融价值的本质吗？你想掌握资本市场的规律吗？你想学习财富管理的方法吗？来吧，加入我们，让我们一起解密价值起源，揭示市场规律，启发财富创造！你将认识货币和资本的力量、你将看到金融创新如何改变了这个世界、 你将理解金融学的几个里程碑、 你将掌握财富增值的路径！', '2021-06-15', '2021-12-23', '经济管理', 1, '《金融学基础》');
INSERT INTO `course` VALUES (12, '100017', '解读中国经济发展的密码', '本课程紧扣时代主题，总结中国发展辉煌成就，解读中国经济发展规律，阐释中国未来发展新理念，新趋势和新目标，帮助学生用政治经济学的思维逻辑，掌握解读中国经济运行和经济发展的密码，能够透彻理解经济形势和政策，把握国家发展大势，更好地将个人发展与国家发展紧密结合起来，做好自己的职业规划。', '2020-06-01', '2021-06-25', '经济管理', 2, '《解读中国经济发展的密码》');
INSERT INTO `course` VALUES (13, '100018', '工科学术英语读写与交流', '此课程可有效帮助有学术英语需求的人士特别是工科院校本科学生在学术英语阅读能力、学术英语论文写作及参加国际会议听说方面得到专业指导，全面培养学生的学术英语实际应用能力。掌握了这些技能，英语就成为您进行学术交流的得力工具！非常期待您能参与到这门课程中来！', '2021-05-13', '2021-08-28', '英语', 3, '《工科学术英语读写与交流》');
INSERT INTO `course` VALUES (14, '100019', '表达与沟通：海外留学英语', '《表达与沟通：海外留学英语》课程以学生出国交流、访学生活为主线，展示在英语国家学术交流生活的各方面知识和场景对话，让学生练习在比较真实的情景中进行口头表达和沟通的能力，以满足学生提高英语学术交流能力的需求。本课程适合非英语专业硕士生、博士生及高年级本科生学习。', '2021-03-01', '2021-09-25', '英语', 1, '《海外留学英语》');
INSERT INTO `course` VALUES (15, '100020', '英汉交替传译', '本课程旨在通过讲授交替传译基本技巧以及对学生进行实用、典型的交替传译场景的训练，使学生初步掌握交替传译的性质和工作原理，提高学生的逻辑思维能力、语言组织能力及双语表达能力，成为基础口译岗位上的合格从业人员。', '2020-12-15', '2021-10-01', '英语', 7, '《英汉交替传译》');
INSERT INTO `course` VALUES (16, '100021', '动物营养学', '四川农业大学动物营养学课程由动物营养国家级教学团队，以国家级规划教材《动物营养学》为蓝本，系统讲授动物营养基础理论、基本研究方法以及生长、繁殖、泌乳、产蛋和产毛动物营养需要，帮助学员高效全面掌握从事动物营养相关研究或从事饲料配方设计、加工以及动物健康养殖必备的理论与方法基础。', '2021-08-01', '2022-03-15', '农林园艺', 3, '《动物营养学》');

